Method.Implementation:
zn "%SYS" Set LowName = $ZCVT(Name,"L")
//Create directories Set Path = ##class(%File).ManagerDirectory() _ Name Set st = ##class(%File).CreateDirectory(Path) Return:$$$ISERR(st) st Set PathWEB = ##class(%File).NormalizeDirectory(##class(%File).ParentDirectoryName(##class(%File).ManagerDirectory()) _ "CSP") _ Name Set st = ##class(%File).CreateDirectory(PathWEB) Return:$$$ISERR(st) st
// Create DataBase File Set st=##Class(SYS.Database).CreateDatabase(Path) Return:$$$ISERR(st) st
// Mount database to Cache Set Properties("Directory")=Path Set Properties("MountRequired")=1 Set st=##Class(Config.Databases).Create(Name,.Properties) Return:$$$ISERR(st) st Set Properties("MountRequired")=1 Set st=##Class(Config.Databases).Modify(Name,.Properties)
// Create NameSpace Set nsProperties("Globals") = Name Set nsProperties("Library") = "CACHELIB" Set nsProperties("Routines") = Name Set nsProperties("SysGlobals") = "CACHESYS" Set nsProperties("SysRoutines") = "CACHESYS" Set nsProperties("TempGlobals") = "CACHETEMP" Set st = ##Class(Config.Namespaces).Create(Name,.nsProperties) Return:$$$ISERR(st) st
// Create web apps
// default if ((WebApps = 1) || (WebApps = -1)) { Set cspProperties("AutheEnabled") = 64 Set cspProperties("NameSpace") = Name Set cspProperties("Path") = PathWEB Set cspProperties("IsNameSpaceDefault") = 1 Set st = ##Class(Security.Applications).Create("/csp/"_LowName,.cspProperties) Return:$$$ISERR(st) st }
// broker if ((WebApps = 2) || (WebApps = -1)) { Set cspProperties("AutheEnabled") = 64 Set cspProperties("NameSpace") = Name Set cspProperties("DispatchClass") = "REST.Broker" Set cspProperties("IsNameSpaceDefault") = 0 Set st = ##Class(Security.Applications).Create("/broker/"_LowName,.cspProperties) Return:$$$ISERR(st) st }
zn Name Return $$$OK
